for i in $(cat cleaner.csv | sed '1d' | cut -d, -f1,6 | sort | uniq); do echo $i; for pv in $(cat cleaner.csv | sed '1d' | cut -d, -f1,4,6 | fgrep "$(echo $i | cut -d, -f1)" | fgrep "$(echo $i | cut -d, -f2)" | sort | uniq | shuf -n 5 --random-source=<(cat /home/user/iFixFlakies_python/victims_brittles.csv)); do echo $pv; for line in $(cat cleaner.csv | sed '1d' | fgrep "$(echo $pv | cut -d, -f1)" | fgrep "$(echo $pv | cut -d, -f2)" | fgrep "$(echo $pv | cut -d, -f3)" | sort | uniq | sed '1,2p'); do echo $line >> cleaners_tcm.csv; done; done; done